import React, { useState } from 'react';

function getRandom(min, max) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min)) + min;
}

const StickyForm = () => {

    const [color, setColor] = useState("");
    const [task, setTask] = useState("");

    const handleSubmit = (event) => {
        event.preventDefault();
        createNewTask();

    }

    const getId = () => {
        let id = getRandom();

        for (let n = 0; n < localStorage.length; n++) {
            const local = localStorage.getItem(n);
            if (local) {
                console.log(id)
            }
        }
    }

    const createNewTask = () => {
        let id = getId();

        console.log(id)

        localStorage.setItem(id, JSON.stringify({
            id: id,
            task: task,
            color: color,
            pending: true
        }))
    }

    const handleClean = () => {
        localStorage.clear()
    }

    console.log(task);

    return (
        <div>
            <form onSubmit={e => handleSubmit(e)}>
                <input placeholder="Nova Tarefa" onChange={e => setTask(e.target.value)} />
                <select name="colors" id="colors" onChange={e => setColor(e.target.value)}>
                    <option value="yellow">Amarelo</option>
                    <option value="green">Verde</option>
                    <option value="red">Vermelho</option>
                    <option value="purple">Roxo</option>
                    <option value="orange">Laranja</option>
                </select>
                <button>Cadastrar</button>
            </form>

            <button onClick={() => handleClean()}>Limpar tarefas</button>
        </div>
    );
}

export default StickyForm;
