import React, { useEffect } from 'react';
import StickyNote from '../StickyNote';
import './PendingTasks.css';

const PendingTasks = () => {

    const loadTasks = () => {
        const dataArr = [];

        let local = JSON.parse(localStorage.getItem(1));


        return dataArr.map(item => <StickyNote key={item.key} color={item.color}>{item.task}</StickyNote>);

    }


    useEffect(() => {
        loadTasks();
    }
    )
    return (
        <div className="PendingTasks">
            {
                loadTasks()
            }
        </div>
    );
}

export default PendingTasks;
