import React from 'react';
import StickyNote from '../StickyNote';

const PendingTasks = () => {

    const loadTasks = () => {
        const dataArr = [];

        for (let n = 0; n < localStorage.length; n++) {
            let data = JSON.parse(localStorage.getItem(n));

            if(data){
                console.log(data);
                dataArr.push(data);
            }
        }
        console.log(dataArr);
        dataArr.map(data => {
            return <StickyNote color={data.color}>{data.task}</StickyNote>
        })

    }


    return (
        <div>
            {
                loadTasks()
            }
        </div>
    );
}

export default PendingTasks;
