import React from 'react';
import './StickyNote.css';

const StickyNote = (props) => {
    const handleDone = () => {
        const task = JSON.parse(localStorage.getItem(props.key));
        task.pending = false;
    }

    return (
        <div onDoubleClick={() => handleDone()} className={`sticky ${props.color}`}>
            {props.children}
        </div>
    );
}

export default StickyNote;
