import React, { useEffect } from 'react';
import StickyNote from '../StickyNote';

const PendingTasks = () => {

    const loadTasks = () => {
        const dataArr = [];

        for (let n = 0; n < localStorage.length; n++) {
            let data = JSON.parse(localStorage.getItem(n));

            if(data){
                dataArr.push(data);
            }
        }

        return dataArr.map(item => <StickyNote key={item.key} color={item.color}>{item.task}</StickyNote>)
    }

    useEffect(()=> {
        loadTasks();
    },[])

    return (
        <div>
            {
                loadTasks()
            }
        </div>
    );
}

export default PendingTasks;
